import React from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { delItem } from "../redux/actions/index";

const WatchList = () => {
  const state = useSelector((state) => state.addItem);
  const dispatch = useDispatch();

  const wishListItems = (watchlistItem) => {
    return (
      <div className="px-4 my-5 bg-light rounded-3 key={watchlistItem.id}">
        <div className="container py-4">
          <button className="btn-close float-end "></button>
          <div className="col-md-4">
            <img src={watchlistItem.img} />
          </div>
          <div className="row">
            <div className="col-md-4">
              <h3></h3>
              <p></p>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return <>{state.length !== 0 && state.map(wishListItems)}</>;
};

export default WatchList;
